it('Creates an exception item when none exist', () => {
  // when no exceptions exist, empty component shows with action to add exception
  cy.get(NO_EXCEPTIONS_EXIST_PROMPT).should('exist'); // clicks prompt button to add first exception that will also select to close
  // all matching alerts

  addFirstExceptionFromRuleDetails({
    field: 'agent.name',
    operator: 'is',
    values: ['foo']
  }); // new exception item displays

  cy.get(EXCEPTION_ITEM_VIEWER_CONTAINER).should('have.length', 1); // Alerts table should now be empty from having added exception and closed
  // matching alert

  goToAlertsTab();
  cy.get(EMPTY_ALERT_TABLE).should('exist'); // Closed alert should appear in table

  goToClosedAlerts();
  cy.get(ALERTS_COUNT).should('exist');
  cy.get(NUMBER_OF_ALERTS).should('have.text', `${NUMBER_OF_AUDITBEAT_EXCEPTIONS_ALERTS}`); // Remove the exception and load an event that would have matched that exception
  // to show that said exception now starts to show up again

  goToExceptionsTab(); // when removing exception and again, no more exist, empty screen shows again

  removeException();
  cy.get(NO_EXCEPTIONS_EXIST_PROMPT).should('exist'); // load more docs

  esArchiverLoad('exceptions_2'); // now that there are no more exceptions, the docs should match and populate alerts

  goToAlertsTab();
  goToOpenedAlerts();
  waitForTheRuleToBeExecuted();
  waitForAlertsToPopulate();
  cy.get(ALERTS_COUNT).should('exist');
  cy.get(NUMBER_OF_ALERTS).should('have.text', '2 alerts');
});

it('Edits an exception item', () => {
  // displays existing exception item
  cy.get(EXCEPTION_ITEM_VIEWER_CONTAINER).should('have.length', 1);
