  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(0).should('have.text', ITEM_FIELD); // check that you can select a different field

  editException(FIELD_DIFFERENT_FROM_EXISTING_ITEM_FIELD, 0, 0);
  cy.get(EXCEPTION_ITEM_VIEWER_CONTAINER).should('have.length', 1); // Alerts table should still show single alert

  goToAlertsTab();
  cy.get(ALERTS_COUNT).should('exist');
