test('babel-plugin-jsx-pragmatic should throw error when invalid options', () => {
  expect(() => {
    transformSync('<></>', {
      filename: __filename,
      plugins: [[jsxPragmatic, {
        export: 'jsx'
      }]]
    });
  }).toThrow('@emotion/babel-plugin-jsx-pragmatic: You must specify `module` and `import`');
});

test('it works', () => {
  let css = `color:hotpink;`;
  let hash = hashString(css);
  safeQuerySelector('body').innerHTML = `<style data-emotion="css ${hash}">.css-${hash}{${css}}</style>`;
  let cache = createCache({
    key: 'css'
  });
  expect(cache.inserted).toEqual({
    [hash]: true
  });
  expect(document.documentElement).toMatchSnapshot();
});

test('rehydrated styles to head can be flushed', () => {
  let css = `color:hotpink;`;
  let hash = hashString(css);
  safeQuerySelector('head').innerHTML = `<style data-emotion="emo ${hash}">.emo-${hash}{${css}}</style>`;

  // this moves emotion style tags at initialization time
  jest.resetModules();
  require('@emotion/react');
  let cache = createCache({
    key: 'emo'
  });
  expect(document.documentElement).toMatchSnapshot();
  cache.sheet.flush();
  expect(document.documentElement).toMatchSnapshot();
});

test('flushing rehydrated styles in the head only affect styles matching the cache key', () => {
