  // this moves emotion style tags at initialization time
  jest.resetModules();
  require('@emotion/react');
  const cache = createCache({
    key: 'emo'
  });
  expect(document.documentElement).toMatchInlineSnapshot(`
    <html>
      <head>
        <style
          data-emotion="css qweqwee"
          data-s=""
        >
          .css-qweqwee{color:red;}
        </style>
        <style
