  app.get('/bar', function (req, res) {
    assert(0);
  });
  app.get('/foo', function (req, res, next) {
    calls.push('/foo');
    next(new Error('fail'));
  });
  app.get('/foo', function (req, res, next) {
    assert(0);
  });
  app.use(function (err, req, res, next) {
    res.json({
      calls: calls,
      error: err.message
    });
  });
  request(app).get('/foo').expect(200, {
    calls: ['/foo/:bar?', '/foo'],
    error: 'fail'
  }, done);
});

