it('should parse deep object', function (done) {
  var str = 'foo';
  for (var i = 0; i < 500; i++) {
    str += '[p]';
  }
  str += '=bar';
  request(this.app).post('/').set('Content-Type', 'application/x-www-form-urlencoded').send(str).expect(function (res) {
    var obj = JSON.parse(res.text);
    assert.strictEqual(Object.keys(obj).length, 1);
    assert.strictEqual(typeof obj.foo, 'object');
    var depth = 0;
    var ref = obj.foo;
    while (ref = ref.p) {
      depth++;
    }
    assert.strictEqual(depth, 500);
  }).expect(200, done);
});

