it('should append multiple headers', function (done) {
  var app = express();
  app.use(function (req, res, next) {
    res.append('Set-Cookie', 'foo=bar');
    next();
  });
  app.use(function (req, res) {
    res.append('Set-Cookie', 'fizz=buzz');
    res.end();
  });
  request(app).get('/').expect(200).expect(shouldHaveHeaderValues('Set-Cookie', ['foo=bar', 'fizz=buzz'])).end(done);
});

