it('should presist store', function (done) {
  var app = express();
  var cb = after(2, done);
  var store = {
    foo: 'bar'
  };
  app.use(function (req, res, next) {
    req.asyncLocalStorage = new asyncHooks.AsyncLocalStorage();
    req.asyncLocalStorage.run(store, next);
  });
  app.use(function (req, res) {
    res.download('test/fixtures/name.txt', function (err) {
      if (err) return cb(err);
      var local = req.asyncLocalStorage.getStore();
      assert.strictEqual(local.foo, 'bar');
      cb();
    });
  });
  request(app).get('/').expect('Content-Type', 'text/plain; charset=UTF-8').expect('Content-Disposition', 'attachment; filename="name.txt"').expect(200, 'tobi', cb);
});

it('should presist store on error', function (done) {
  var app = express();
  var store = {
    foo: 'bar'
  };
  app.use(function (req, res, next) {
    req.asyncLocalStorage = new asyncHooks.AsyncLocalStorage();
    req.asyncLocalStorage.run(store, next);
  });
  app.use(function (req, res) {
    res.download('test/fixtures/does-not-exist', function (err) {
      var local = req.asyncLocalStorage.getStore();
      if (local) {
        res.setHeader('x-store-foo', String(local.foo));
      }
      res.send(err ? 'got ' + err.status + ' error' : 'no error');
    });
  });
  request(app).get('/').expect(200).expect('x-store-foo', 'bar').expect('got 404 error').end(done);
});

