test('validate good redirects without throwing', () => {
  validateRedirect({
    from: '/fromSomePath',
    to: '/toSomePath',
  });
  validateRedirect({
    from: '/from/Some/Path',
    to: '/toSomePath',
  });
  validateRedirect({
    from: '/fromSomePath',
    to: '/toSomePath',
  });
  validateRedirect({
    from: '/fromSomePath',
