      version: {
        ...arg.version,
        contentPath: path.relative(siteDir, arg.version.contentPath),
      },
    };
  }

  expect(makeDeterministic(generatorArg)).toMatchSnapshot();
  expect(generatorArg.defaultSidebarItemsGenerator).toEqual(
    DefaultSidebarItemsGenerator,
  );
});

