  expect(applyTrailingSlash('abc', params(undefined))).toEqual('abc');
  expect(applyTrailingSlash('abc/', params(true))).toEqual('abc/');
  expect(applyTrailingSlash('abc/', params(false))).toEqual('abc');
  expect(applyTrailingSlash('abc/', params(undefined))).toEqual('abc/');
  expect(applyTrailingSlash('/abc', params(true))).toEqual('/abc/');
  expect(applyTrailingSlash('/abc', params(false))).toEqual('/abc');
  expect(applyTrailingSlash('/abc', params(undefined))).toEqual('/abc');
  expect(applyTrailingSlash('/abc/', params(true))).toEqual('/abc/');
  expect(applyTrailingSlash('/abc/', params(false))).toEqual('/abc');
  expect(applyTrailingSlash('/abc/', params(undefined))).toEqual('/abc/');
});

