    schema: PluginIdSchema,
    defaultValue: 'default',
  });

  testOK(undefined);
  testOK('docs');
  testOK('default');
  testOK('plugin-id_with-simple-special-chars');

  testFail('/docs');
  testFail('docs/');
  testFail('do/cs');
  testFail('do cs');
  testFail(null);
  testFail(3);
  testFail(true);
  testFail([]);
});

test('AdmonitionsSchema', () => {
