  });

  // Only generate short unique id
  const thirdAssert: Record<string, string> = {
    a: '0cc175b9',
    b: '92eb5ffe',
    c: '4a8a08f0',
    d: '8277e091',
  };
  Object.keys(thirdAssert).forEach((str) => {
    expect(genChunkName(str, undefined, undefined, true)).toBe(
      thirdAssert[str],
    );
  });
