test('Should allow elements to be inspected', async () => {
  // Select the first list item in DevTools.
  await devToolsUtils.selectElement(page, 'ListItem', 'List\nApp');

  // Prop names/values may not be editable based on the React version.
  // If they're not editable, make sure they degrade gracefully
  const isEditableName = semver.gte(config.use.react_version, '17.0.0');
  const isEditableValue = semver.gte(config.use.react_version, '16.8.0');

  // Then read the inspected values.
  const [propName, propValue, sourceText] = await page.evaluate(isEditable => {
    const {
      createTestNameSelector,
      findAllNodes
    } = window.REACT_DOM_DEVTOOLS;
    const container = document.getElementById('devtools');

    // Get name of first prop
    const selectorName = isEditable.name ? 'EditableName' : 'NonEditableName';
    const nameElement = findAllNodes(container, [createTestNameSelector('InspectedElementPropsTree'), createTestNameSelector(selectorName)])[0];
    const name = isEditable.name ? nameElement.value : nameElement.innerText;

    // Get value of first prop
    const selectorValue = isEditable.value ? 'EditableValue' : 'NonEditableValue';
    const valueElement = findAllNodes(container, [createTestNameSelector('InspectedElementPropsTree'), createTestNameSelector(selectorValue)])[0];
    const source = findAllNodes(container, [createTestNameSelector('InspectedElementView-Source')])[0];
    const value = isEditable.value ? valueElement.value : valueElement.innerText;
    return [name, value, source.innerText];
  }, {
    name: isEditableName,
    value: isEditableValue
  });
  expect(propName).toBe('label');
  expect(propValue).toBe('"one"');
  expect(sourceText).toMatch(/ListApp[a-zA-Z]*\.js/);
});

