test('should allow searching for component by name', async () => {
  async function getComponentSearchResultsCount() {
    return await page.evaluate(() => {
      const {
        createTestNameSelector,
        findAllNodes
      } = window.REACT_DOM_DEVTOOLS;
      const container = document.getElementById('devtools');
      const element = findAllNodes(container, [createTestNameSelector('ComponentSearchInput-ResultsCount')])[0];
      return element.innerText;
    });
  }

  async function focusComponentSearch() {
    await page.evaluate(() => {
      const {
        createTestNameSelector,
        focusWithin
      } = window.REACT_DOM_DEVTOOLS;
      const container = document.getElementById('devtools');
      focusWithin(container, [createTestNameSelector('ComponentSearchInput-Input')]);
    });
  }

  await focusComponentSearch();
  page.keyboard.insertText('List');
  let count = await getComponentSearchResultsCount();
  expect(count).toBe('1 | 4');
  await focusComponentSearch();
  page.keyboard.insertText('Item');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('1 | 3');
  page.keyboard.press('Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('2 | 3');
  page.keyboard.press('Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('3 | 3');
  page.keyboard.press('Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('1 | 3');
  page.keyboard.press('Shift+Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('3 | 3');
  page.keyboard.press('Shift+Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('2 | 3');
  page.keyboard.press('Shift+Enter');
  count = await getComponentSearchResultsCount();
  expect(count).toBe('1 | 3');
});

test('should record renders and commits when active', async () => {
  // Profiling is only available in 16.5 and over
  runOnlyForReactRange('>=16.5');

  async function getSnapshotSelectorText() {
    return await page.evaluate(() => {
      const {
        createTestNameSelector,
        findAllNodes
      } = window.REACT_DOM_DEVTOOLS;
      const container = document.getElementById('devtools');
      const input = findAllNodes(container, [createTestNameSelector('SnapshotSelector-Input')])[0];
      const label = findAllNodes(container, [createTestNameSelector('SnapshotSelector-Label')])[0];
      return `${input.value}${label.innerText}`;
    });
  }

  async function clickButtonAndVerifySnapshotSelectorText(buttonTagName, expectedText) {
    await devToolsUtils.clickButton(page, buttonTagName);
    const text = await getSnapshotSelectorText();
    expect(text).toBe(expectedText);
  }

  await devToolsUtils.clickButton(page, 'ProfilerToggleButton');
  await listAppUtils.addItem(page, 'four');
  await listAppUtils.addItem(page, 'five');
  await listAppUtils.addItem(page, 'six');
  await devToolsUtils.clickButton(page, 'ProfilerToggleButton');
  await page.waitForFunction(() => {
    const {
      createTestNameSelector,
      findAllNodes
    } = window.REACT_DOM_DEVTOOLS;
    const container = document.getElementById('devtools');
    const input = findAllNodes(container, [createTestNameSelector('SnapshotSelector-Input')]);
    return input.length === 1;
  });
  const text = await getSnapshotSelectorText();
  expect(text).toBe('1 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-NextButton', '2 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-NextButton', '3 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-NextButton', '1 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-PreviousButton', '3 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-PreviousButton', '2 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-PreviousButton', '1 / 3');
  await clickButtonAndVerifySnapshotSelectorText('SnapshotSelector-PreviousButton', '3 / 3');
});
