      t.same(payload, {
        preHandler: true,
        hello: 'world'
      });
    });
  }); // https://github.com/fastify/fastify/issues/2229

  t.test('preHandler hook in setNotFoundHandler should be called when callNotFound', {
    timeout: 40000
  }, t => {
    t.plan(2);
    const fastify = Fastify();
    fastify.setNotFoundHandler({
      preHandler: (req, reply, done) => {
        req.body.preHandler = true;
        done();
      }
    }, function (req, reply) {
      reply.code(404).send(req.body);
    });
    fastify.post('/', function (req, reply) {
      reply.callNotFound();
    });
    fastify.inject({
      method: 'POST',
      url: '/',
      payload: {
        hello: 'world'
      }
    }, (err, res) => {
      t.error(err);
      const payload = JSON.parse(res.payload);
      t.same(payload, {
        preHandler: true,
        hello: 'world'
      });
    });
  });
  t.test('preHandler hook in setNotFoundHandler should accept an array of functions and be called when callNotFound', t => {
    t.plan(2);
    const fastify = Fastify();
    fastify.setNotFoundHandler({
      preHandler: [(req, reply, done) => {
        req.body.preHandler1 = true;
        done();
      }, (req, reply, done) => {
        req.body.preHandler2 = true;
        done();
      }]
    }, function (req, reply) {
      reply.code(404).send(req.body);
    });
    fastify.post('/', function (req, reply) {
      reply.callNotFound();
    });
    fastify.inject({
      method: 'POST',
      url: '/',
      payload: {
        hello: 'world'
      }
    }, (err, res) => {
      t.error(err);
      const payload = JSON.parse(res.payload);
      t.same(payload, {
        preHandler1: true,
        preHandler2: true,
        hello: 'world'
      });
    });
  });
  t.test('preHandler option should be called after preHandler hook', t => {
    t.plan(2);
    const fastify = Fastify();
    fastify.addHook('preHandler', (req, reply, done) => {
      req.body.check = 'a';
      done();
    });
    fastify.setNotFoundHandler({
      preHandler: (req, reply, done) => {
        req.body.check += 'b';
        done();
      }
    }, (req, reply) => {
      reply.send(req.body);
    });
    fastify.inject({
      method: 'POST',
      url: '/',
      payload: {
        hello: 'world'
      }
    }, (err, res) => {
      t.error(err);
      const payload = JSON.parse(res.payload);
      t.same(payload, {
        check: 'ab',
        hello: 'world'
      });
    });
  });
  t.test('preHandler option should be unique per prefix', t => {
    t.plan(4);
