        done();
      }
    }, (req, reply) => {
      reply.send(req.body);
    });
    fastify.register(function (i, o, n) {
      i.setNotFoundHandler((req, reply) => {
        reply.send(req.body);
      });
      n();
    }, {
      prefix: '/no'
    });
    fastify.inject({
      method: 'POST',
      url: '/not-found',
