        get: secret => {
          return secrets[secret] || null;
        },
        set: (secret, store) => {
          secrets[secret] = store;
        },
        del: secret => {
          delete secrets[secret];
        },
        empty: () => {
          secrets = {};
        }
      };
    },
    deriveConstraint: (req, ctx) => {
      return req.headers['x-secret'];
