    instance.get('/res-decorated-get-set', (req, res) => {
      res.send({
        test: res.test
      });
    });
    done();
  });
  fastify.get('/not-decorated', (req, res) => {
    t.notOk(res.test);
    res.send();
  });
  fastify.ready(() => {
    fastify.inject({
      url: '/res-decorated-get-set'
    }, (err, res) => {
      t.error(err);
      t.same(JSON.parse(res.payload), {
        test: 'a getter'
      });
    });
    fastify.inject({
      url: '/not-decorated'
    }, (err, res) => {
      t.error(err);
      t.pass();
    });
  });
});

