});

test('use fluent-json-schema object', t => {
  t.plan(15);
  const fastify = Fastify();
  fastify.post('/:id', {
    handler: (req, reply) => {
      reply.send({
        name: 'a',
        surname: 'b',
        dateOfBirth: '01-01-2020'
      });
    },
    schema: {
      params: S.object().prop('id', S.integer().minimum(42)),
      headers: S.object().prop('x-custom', S.string().format('email')),
      query: S.object().prop('surname', S.string().required()),
      body: S.object().prop('name', S.string().required()),
      response: {
        200: S.object().prop('name', S.string()).prop('surname', S.string())
      }
    }
  }); // check params

  fastify.inject({
    method: 'POST',
    url: '/1',
    headers: {
      'x-custom': 'me@me.me'
    },
    query: {
      surname: 'bar'
    },
    payload: {
      name: 'foo'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
    t.same(res.json(), {
      statusCode: 400,
      error: 'Bad Request',
      message: 'params.id should be >= 42'
    });
  }); // check header

  fastify.inject({
    method: 'POST',
    url: '/42',
    headers: {
      'x-custom': 'invalid'
    },
    query: {
      surname: 'bar'
    },
    payload: {
      name: 'foo'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
    t.same(res.json(), {
      statusCode: 400,
      error: 'Bad Request',
      message: 'headers[\'x-custom\'] should match format "email"'
    });
  }); // check query

  fastify.inject({
    method: 'POST',
    url: '/42',
    headers: {
      'x-custom': 'me@me.me'
    },
    query: {},
    payload: {
      name: 'foo'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
    t.same(res.json(), {
      statusCode: 400,
      error: 'Bad Request',
      message: 'querystring should have required property \'surname\''
    });
  }); // check body

  fastify.inject({
    method: 'POST',
    url: '/42',
    headers: {
      'x-custom': 'me@me.me'
    },
    query: {
      surname: 'bar'
    },
    payload: {
      name: [1, 2, 3]
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
    t.same(res.json(), {
      statusCode: 400,
      error: 'Bad Request',
      message: 'body.name should be string'
    });
  }); // check response

  fastify.inject({
    method: 'POST',
    url: '/42',
    headers: {
      'x-custom': 'me@me.me'
    },
    query: {
      surname: 'bar'
    },
    payload: {
      name: 'foo'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
      name: 'a',
      surname: 'b'
    });
  });
});

