      t.equal(response.statusCode, 200);
      t.equal(response.headers['content-length'], '' + body.length);
      t.same(JSON.parse(body), {
        hello: 'world1'
      });
    });
    sget({
      method: 'GET',
      url: 'http://localhost:' + fastify.server.address().port + '/second'
    }, (err, response, body) => {
      t.error(err);
      t.equal(response.statusCode, 200);
      t.equal(response.headers['content-length'], '' + body.length);
      t.same(JSON.parse(body), {
        hello: 'world12'
      });
