      listening: false,
      closing: false,
      started: false
    }
  };
  decorator.add.call(one, 'foo', {
    getter: () => this._a,
    setter: val => {
      t.pass();
      this._a = val;
    }
  });
  t.equal(one.hasOwnProperty('foo'), true);
  t.equal(one.foo, undefined);
  one.foo = 'a';
  t.equal(one.foo, 'a'); // getter only

  const two = {
    [kState]: {
      listening: false,
      closing: false,
      started: false
    }
  };
  decorator.add.call(two, 'foo', {
    getter: () => 'a getter'
  });
  t.equal(two.hasOwnProperty('foo'), true);
  t.equal(two.foo, 'a getter');
});

