  fastify.get('/one', (req, reply) => {
    t.equal(req.id, REQUEST_ID);
    req.log.info('some log message');
    reply.send({
      id: req.id
    });
  });
  fastify.get('/two', (req, reply) => {
    t.equal(req.id, 'foo');
    req.log.info('some log message 2');
    reply.send({
      id: req.id
    });
  });
  const matches = [{
    reqId: REQUEST_ID,
    msg: /incoming request/
  }, {
    reqId: REQUEST_ID,
    msg: /some log message/
  }, {
    reqId: REQUEST_ID,
    msg: /request completed/
  }, {
    reqId: 'foo',
    msg: /incoming request/
  }, {
    reqId: 'foo',
    msg: /some log message 2/
  }, {
    reqId: 'foo',
    msg: /request completed/
  }];
  let i = 0;
  stream.on('data', line => {
    t.match(line, matches[i]);
    i += 1;
  });
  fastify.inject({
    method: 'GET',
    url: '/one',
    headers: {
      'my-custom-request-id': REQUEST_ID
    }
  }, (err, res) => {
    t.error(err);
    const payload = JSON.parse(res.payload);
    t.equal(payload.id, REQUEST_ID);
  });
  fastify.inject({
    method: 'GET',
    url: '/two'
  }, (err, res) => {
