test('pretty print - includeMeta, includeHooks', t => {
  t.plan(6);
  const fastify = Fastify();

  const onTimeout = () => {};

  fastify.get('/hello', () => {});
  fastify.put('/hello', () => {});
  fastify.get('/helicopter', () => {});
  fastify.addHook('onRequest', () => {});
  fastify.addHook('onTimeout', onTimeout);
  fastify.ready(() => {
    const radixTree = fastify.printRoutes({
      includeHooks: true,
      includeMeta: ['errorHandler']
    });
    const flatTree = fastify.printRoutes({
      commonPrefix: false,
      includeHooks: true,
      includeMeta: ['errorHandler']
    });
    const hooksOnly = fastify.printRoutes({
      commonPrefix: false,
      includeHooks: true
    });
    const radixExpected = `└── /
    ├── hel
    │   ├── lo (GET)
    │   │   • (onTimeout) ["onTimeout()"]
    │   │   • (onRequest) ["anonymous()"]
    │   │   • (errorHandler) "defaultErrorHandler()"
    │   └── icopter (GET)
    │       • (onTimeout) ["onTimeout()"]
    │       • (onRequest) ["anonymous()"]
    │       • (errorHandler) "defaultErrorHandler()"
    └── hello (PUT)
        • (onTimeout) ["onTimeout()"]
        • (onRequest) ["anonymous()"]
        • (errorHandler) "defaultErrorHandler()"
`;
    const flatExpected = `└── / (-)
    ├── helicopter (GET)
    │   • (onTimeout) ["onTimeout()"]
    │   • (onRequest) ["anonymous()"]
    │   • (errorHandler) "defaultErrorHandler()"
    └── hello (GET, PUT)
        • (onTimeout) ["onTimeout()"]
        • (onRequest) ["anonymous()"]
        • (errorHandler) "defaultErrorHandler()"
`;
    const hooksOnlyExpected = `└── / (-)
    ├── helicopter (GET)
    │   • (onTimeout) ["onTimeout()"]
    │   • (onRequest) ["anonymous()"]
    └── hello (GET, PUT)
        • (onTimeout) ["onTimeout()"]
        • (onRequest) ["anonymous()"]
`;
    t.equal(typeof radixTree, 'string');
    t.equal(typeof flatTree, 'string');
    t.equal(typeof hooksOnlyExpected, 'string');
    t.equal(radixTree, radixExpected);
    t.equal(flatTree, flatExpected);
    t.equal(hooksOnly, hooksOnlyExpected);
  });
});

