test('does not mutate joi schemas', t => {
  t.plan(4);
  const fastify = Fastify();

  function validatorCompiler({
    schema,
    method,
    url,
    httpPart
  }) {
    // Needed to extract the params part,
    // without the JSON-schema encapsulation
    // that is automatically added by the short
    // form of params.
    schema = joi.object(schema.properties);
    return validateHttpData;

    function validateHttpData(data) {
      return schema.validate(data);
    }
  }

  fastify.setValidatorCompiler(validatorCompiler);
  fastify.route({
    path: '/foo/:an_id',
    method: 'GET',
    schema: {
      params: {
        an_id: joi.number()
      }
    },

    handler(req, res) {
      t.same(req.params, {
        an_id: 42
      });
      res.send({
        hello: 'world'
      });
    }

  });
  fastify.inject({
    method: 'GET',
    url: '/foo/42'
  }, (err, result) => {
    t.error(err);
    t.equal(result.statusCode, 200);
    t.same(JSON.parse(result.payload), {
      hello: 'world'
    });
  });
});

