    method: 'GET',
    path: '/json',
    handler: (req, reply) => {
      reply.send(resJSON);
    }
  });
  fastify.route({
    method: 'GET',
    path: '/string',
    handler: (req, reply) => {
      reply.send(resString);
    }
  });
  fastify.route({
    method: 'GET',
    path: '/buffer',
    handler: (req, reply) => {
      reply.send(resBuffer);
    }
  });
  fastify.route({
    method: 'GET',
    path: '/buffer-with-content-type',
    handler: (req, reply) => {
      reply.headers({
        'content-type': 'image/jpeg'
      });
      reply.send(resBuffer);
    }
  });
  fastify.route({
    method: 'GET',
    path: '/stream',
    handler: (req, reply) => {
      return resStream;
    }
  });
  fastify.inject({
    method: 'HEAD',
    url: '/json'
  }, (error, res) => {
    t.error(error);
    t.equal(res.statusCode, 200);
    t.equal(res.headers['content-type'], 'application/json; charset=utf-8');
    t.equal(res.headers['content-length'], `${Buffer.byteLength(JSON.stringify(resJSON))}`);
    t.same(res.body, '');
  });
  fastify.inject({
    method: 'HEAD',
    url: '/string'
  }, (error, res) => {
    t.error(error);
    t.equal(res.statusCode, 200);
    t.equal(res.headers['content-type'], 'text/plain; charset=utf-8');
    t.equal(res.headers['content-length'], `${Buffer.byteLength(resString)}`);
    t.equal(res.body, '');
  });
  fastify.inject({
    method: 'HEAD',
    url: '/buffer'
  }, (error, res) => {
    t.error(error);
    t.equal(res.statusCode, 200);
    t.equal(res.headers['content-type'], 'application/octet-stream');
    t.equal(res.headers['content-length'], `${resBuffer.byteLength}`);
    t.equal(res.body, '');
  });
  fastify.inject({
    method: 'HEAD',
    url: '/buffer-with-content-type'
  }, (error, res) => {
    t.error(error);
    t.equal(res.statusCode, 200);
    t.equal(res.headers['content-type'], 'image/jpeg');
    t.equal(res.headers['content-length'], `${resBuffer.byteLength}`);
    t.equal(res.body, '');
  });
  fastify.inject({
    method: 'HEAD',
    url: '/stream'
  }, (error, res) => {
    t.error(error);
    t.equal(res.statusCode, 200);
    t.equal(res.headers['content-type'], 'application/octet-stream');
    t.equal(res.headers['content-length'], undefined);
    t.equal(res.body, '');
  });
});

test('HEAD route should respect custom onSend handlers', t => {
