    t.equal(res.body, '');
    t.equal(counter, 2);
  });
});

test('route onSend can be function or array of functions', t => {
  t.plan(12);
  const counters = {
    single: 0,
    multiple: 0
  };
  const resBuffer = Buffer.from('I am a coffee!');
  const fastify = Fastify({
    exposeHeadRoutes: true
  });
  fastify.route({
    method: 'GET',
    path: '/coffee',
    handler: () => resBuffer,
    onSend: (res, reply, payload, done) => {
      counters.single += 1;
      done(null, payload);
    }
  });

  const customOnSend = (res, reply, payload, done) => {
    counters.multiple += 1;
    done(null, payload);
  };

  fastify.route({
    method: 'GET',
    path: '/more-coffee',
    handler: () => resBuffer,
    onSend: [customOnSend, customOnSend]
  });
  fastify.inject({
    method: 'HEAD',
    url: '/coffee'
  }, (error, res) => {
