test('should return localized error messages with ajv-i18n', t => {
  t.plan(3);
  const schema = {
    body: {
      type: 'object',
      properties: {
        name: {
          type: 'string'
        },
        work: {
          type: 'string'
        }
      },
      required: ['name', 'work']
    }
  };
  const fastify = Fastify({
    ajv: {
      customOptions: {
        allErrors: true
      }
    }
  });
  fastify.setErrorHandler(function (error, request, reply) {
    if (error.validation) {
      localize.ru(error.validation);
      reply.status(400).send(error.validation);
      return;
    }

    reply.send(error);
  });
  fastify.post('/', {
    schema
  }, function (req, reply) {
    reply.code(200).send(req.body.name);
  });
  fastify.inject({
    method: 'POST',
    payload: {
      name: 'salman'
    },
    url: '/'
  }, (err, res) => {
    t.error(err);
    t.same(JSON.parse(res.payload), [{
      dataPath: '',
      keyword: 'required',
      message: 'должно иметь обязательное поле work',
      params: {
        missingProperty: 'work'
      },
      schemaPath: '#/required'
    }]);
    t.equal(res.statusCode, 400);
  });
});

