test('Customize validator compiler in instance and route', t => {
  t.plan(28);
  const fastify = Fastify();
  fastify.setValidatorCompiler(({
    schema,
    method,
    url,
    httpPart
  }) => {
    t.equal(method, 'POST'); // run 4 times

    t.equal(url, '/:id'); // run 4 times

    switch (httpPart) {
      case 'body':
        t.pass('body evaluated');
        return body => {
          t.same(body, {
            foo: ['bar', 'BAR']
          });
          return true;
        };

      case 'params':
        t.pass('params evaluated');
        return params => {
          t.same(params, {
            id: 1234
          });
          return true;
        };

      case 'querystring':
        t.pass('querystring evaluated');
        return query => {
          t.same(query, {
            lang: 'en'
          });
          return true;
        };

      case 'headers':
        t.pass('headers evaluated');
        return headers => {
          t.match(headers, {
            x: 'hello'
          });
          return true;
        };

      case '2xx':
        t.fail('the validator doesn\'t process the response');
        break;

      default:
        t.fail(`unknown httpPart ${httpPart}`);
    }
  });
  fastify.post('/:id', {
    handler: echoBody,
    schema: {
      query: {
        lang: {
          type: 'string',
          enum: ['it', 'en']
        }
      },
      headers: {
        x: {
          type: 'string'
        }
      },
      params: {
        id: {
          type: 'number'
        }
      },
      body: {
        foo: {
          type: 'array'
        }
      },
      response: {
        '2xx': {
          foo: {
            type: 'array',
            items: {
              type: 'string'
            }
          }
        }
      }
    }
  });
  fastify.get('/wow/:id', {
    handler: echoParams,
    validatorCompiler: ({
      schema,
      method,
      url,
      httpPart
    }) => {
      t.equal(method, 'GET'); // run 3 times (params, headers, query)

      t.equal(url, '/wow/:id'); // run 4 times

      return () => {
        return true;
      }; // ignore the validation
    },
    schema: {
      query: {
        lang: {
          type: 'string',
          enum: ['it', 'en']
        }
      },
      headers: {
        x: {
          type: 'string'
        }
      },
      params: {
        id: {
          type: 'number'
        }
      },
      response: {
        '2xx': {
          foo: {
            type: 'array',
            items: {
              type: 'string'
            }
          }
        }
      }
    }
  });
  fastify.inject({
    url: '/1234',
    method: 'POST',
    headers: {
      x: 'hello'
    },
    query: {
      lang: 'en'
    },
    payload: {
      foo: ['bar', 'BAR']
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
      foo: ['bar', 'BAR']
    });
  });
  fastify.inject({
    url: '/wow/should-be-a-num',
    method: 'GET',
    headers: {
      x: 'hello'
    },
    query: {
      lang: 'jp'
    } // not in the enum

  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200); // the validation is always true

    t.same(res.json(), {});
  });
});

