test('Encapsulation isolation for getSchemas', t => {
  t.plan(5);
  const fastify = Fastify();
  let pluginDeepOneSide;
  let pluginDeepOne;
  let pluginDeepTwo;
  const schemas = {
    z: {
      $id: 'z',
      my: 'schema'
    },
    a: {
      $id: 'a',
      my: 'schema'
    },
    b: {
      $id: 'b',
      my: 'schema'
    },
    c: {
      $id: 'c',
      my: 'schema',
      properties: {
        a: 'a',
        b: 1
      }
    }
  };
  fastify.addSchema(schemas.z);
  fastify.register((instance, opts, done) => {
    instance.addSchema(schemas.a);
    pluginDeepOneSide = instance;
    done();
  });
  fastify.register((instance, opts, done) => {
    instance.addSchema(schemas.b);
    instance.register((subinstance, opts, done) => {
      subinstance.addSchema(schemas.c);
      pluginDeepTwo = subinstance;
      done();
    });
    pluginDeepOne = instance;
    done();
  });
  fastify.ready(err => {
    t.error(err);
    t.same(fastify.getSchemas(), {
      z: schemas.z
    });
    t.same(pluginDeepOneSide.getSchemas(), {
      z: schemas.z,
      a: schemas.a
    });
    t.same(pluginDeepOne.getSchemas(), {
      z: schemas.z,
      b: schemas.b
    });
    t.same(pluginDeepTwo.getSchemas(), {
      z: schemas.z,
      b: schemas.b,
      c: schemas.c
    });
  });
});

