test('Custom serializer per route', async t => {
  const fastify = Fastify();
  const outSchema = {
    $id: 'test',
    type: 'object',
    properties: {
      mean: {
        type: 'string'
      }
    }
  };
  fastify.get('/default', {
    handler(req, reply) {
      reply.send({
        mean: 'default'
      });
    },

    schema: {
      response: {
        200: outSchema
      }
    }
  });
  let hit = 0;
  fastify.register((instance, opts, done) => {
    instance.setSerializerCompiler(({
      schema,
      method,
      url,
      httpStatus
    }) => {
      hit++;
      return data => JSON.stringify({
        mean: 'custom'
      });
    });
    instance.get('/custom', {
      handler(req, reply) {
        reply.send({});
      },

      schema: {
        response: {
          200: outSchema
        }
      }
    });
    instance.get('/route', {
      handler(req, reply) {
        reply.send({});
      },

      serializerCompiler: ({
        schema,
        method,
        url,
        httpPart
      }) => {
        hit++;
        return data => JSON.stringify({
          mean: 'route'
        });
      },
      schema: {
        response: {
          200: outSchema
        }
      }
    });
    done();
  });
  let res = await fastify.inject('/default');
  t.equal(res.json().mean, 'default');
  res = await fastify.inject('/custom');
  t.equal(res.json().mean, 'custom');
  res = await fastify.inject('/route');
  t.equal(res.json().mean, 'route');
  t.equal(hit, 2, 'the custom and route serializer has been called');
});

