test('Encapsulation', t => {
  t.plan(19);
  const fastify = Fastify();
  const ajv = new AJV();
  ajv.addSchema(schemaA);
  ajv.addSchema(schemaBRefToA); // the user must provide the schemas to fastify also

  fastify.addSchema(schemaA);
  fastify.addSchema(schemaBRefToA);
  fastify.register((instance, opts, done) => {
    const validator = ({
      schema,
      method,
      url,
      httpPart
    }) => {
      return ajv.compile(schema);
    };

    instance.setValidatorCompiler(validator);
    instance.post('/one', {
      handler(req, reply) {
        reply.send({
          foo: 'one'
        });
      },

      schema: {
        body: ajv.getSchema('urn:schema:response').schema
      }
    });
    instance.register((instance, opts, done) => {
      instance.post('/two', {
        handler(req, reply) {
          t.same(instance.validatorCompiler, validator);
          reply.send({
            foo: 'two'
          });
        },

        schema: {
          body: ajv.getSchema('urn:schema:response').schema
        }
      });

      const anotherValidator = ({
        schema,
        method,
        url,
        httpPart
      }) => {
        return () => {
          return true;
        }; // always valid
      };

      instance.post('/three', {
        validatorCompiler: anotherValidator,

        handler(req, reply) {
          t.same(instance.validatorCompiler, validator, 'the route validator does not change the instance one');
          reply.send({
            foo: 'three'
          });
        },

        schema: {
          body: ajv.getSchema('urn:schema:response').schema
        }
      });
      done();
    });
    done();
  });
  fastify.register((instance, opts, done) => {
    instance.post('/clean', function (req, reply) {
      t.equal(instance.validatorCompiler, undefined);
      reply.send({
        foo: 'bar'
      });
    });
    done();
  });
  fastify.inject({
    method: 'POST',
    url: '/one',
    payload: {
      foo: 1
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
      foo: 'one'
    });
  });
  fastify.inject({
    method: 'POST',
    url: '/one',
    payload: {
      wrongFoo: 'bar'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
  });
  fastify.inject({
    method: 'POST',
    url: '/two',
    payload: {
      foo: 2
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
      foo: 'two'
    });
  });
  fastify.inject({
    method: 'POST',
    url: '/two',
    payload: {
      wrongFoo: 'bar'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 400);
  });
  fastify.inject({
    method: 'POST',
    url: '/three',
    payload: {
      wrongFoo: 'but works'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
      foo: 'three'
    });
  });
  fastify.inject({
    method: 'POST',
    url: '/clean',
    payload: {
      wrongFoo: 'bar'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 200);
    t.same(res.json(), {
