    stream.on('data', line => {
      t.not(line.level, 40); // there are no errors

      t.not(line.level, 50); // there are no errors
    });
    previousHooks.forEach(h => app.addHook(h, async (req, reply) => t.pass(`${h} should be called`)));

    if (hookOrHandler === 'handler') {
      app.get('/', (req, reply) => {
