        port: app.server.address().port
      }, () => {
        client.write('GET / HTTP/1.1\r\n\r\n');
        let chunks = '';
        client.setEncoding('utf8');
        client.on('data', data => {
          chunks += data;
        });
        client.on('end', function () {
          t.match(chunks, new RegExp(`hello from ${hookOrHandler}`, 'i'));
          t.end();
        });
      });
    });
  });
