test('The not found handler should not erase the Accept-Version header', t => {
  t.plan(13);
  const fastify = Fastify();
  fastify.addHook('onRequest', function (req, reply, done) {
    t.same(req.headers['accept-version'], '2.x');
    done();
  });
  fastify.addHook('preValidation', function (req, reply, done) {
    t.same(req.headers['accept-version'], '2.x');
    done();
  });
  fastify.addHook('preHandler', function (req, reply, done) {
    t.same(req.headers['accept-version'], '2.x');
    done();
  });
  fastify.route({
    method: 'GET',
    url: '/',
    constraints: {
      version: '1.2.0'
    },
    handler: (req, reply) => {
      reply.send({
        hello: 'world'
      });
    }
  });
  fastify.setNotFoundHandler(function (req, reply) {
    t.same(req.headers['accept-version'], '2.x'); // we check if the symbol is exposed on key or not

    for (const key in req.headers) {
      t.same(typeof key, 'string');
    }

    for (const key of Object.keys(req.headers)) {
      t.same(typeof key, 'string');
    }

    reply.code(404).send('not found handler');
  });
  fastify.inject({
    method: 'GET',
    url: '/',
    headers: {
      'Accept-Version': '2.x'
    }
  }, (err, res) => {
    t.error(err);
    t.same(res.payload, 'not found handler');
    t.equal(res.statusCode, 404);
  });
});

