      port: fastify.server.address().port,
      path: '/',
      method: 'GET',
      headers: {
        'Accept-Version': '1.x'
      }
    });
    stream.once('data', listenAtLogLine => {
      stream.once('data', line => {
        t.equal(line.req.version, '1.x');
        stream.once('data', line => {
          t.equal(line.req.version, '1.x');
        });
      });
    });
  });
});

test('Should register a versioned route with custom versioning strategy', t => {
  t.plan(8);
  const customVersioning = {
    name: 'version',
    storage: function () {
      let versions = {};
      return {
        get: version => {
          return versions[version] || null;
        },
        set: (version, store) => {
          versions[version] = store;
        },
        del: version => {
          delete versions[version];
        },
        empty: () => {
          versions = {};
        }
      };
    },
    deriveConstraint: (req, ctx) => {
      return req.headers.accept;
    },
    mustMatchWhenDerived: true,
    validate: () => true
  };
  const fastify = Fastify({
    constraints: {
      version: customVersioning
    }
  });
  fastify.route({
    method: 'GET',
    url: '/',
    constraints: {
      version: 'application/vnd.example.api+json;version=2'
    },
    handler: (req, reply) => {
      reply.send({
        hello: 'from route v2'
      });
    }
  });
  fastify.route({
    method: 'GET',
    url: '/',
    constraints: {
      version: 'application/vnd.example.api+json;version=3'
    },
    handler: (req, reply) => {
      reply.send({
        hello: 'from route v3'
      });
    }
  });
  fastify.inject({
    method: 'GET',
    url: '/',
    headers: {
      Accept: 'application/vnd.example.api+json;version=2'
    }
  }, (err, res) => {
    t.error(err);
    t.same(JSON.parse(res.payload), {
      hello: 'from route v2'
    });
    t.equal(res.statusCode, 200);
  });
  fastify.inject({
    method: 'GET',
    url: '/',
    headers: {
      Accept: 'application/vnd.example.api+json;version=3'
    }
  }, (err, res) => {
    t.error(err);
    t.same(JSON.parse(res.payload), {
      hello: 'from route v3'
    });
    t.equal(res.statusCode, 200);
  });
  fastify.inject({
    method: 'GET',
    url: '/',
    headers: {
      Accept: 'application/vnd.example.api+json;version=4'
    }
  }, (err, res) => {
    t.error(err);
    t.equal(res.statusCode, 404);
  });
});

