it('should be invokable several times', () => {
  let i = 0;
  const key = cache.registerFunc(() => i++);
  return cache.resolveFunc(key).then(val => {
    expect(val).to.be(0);
  }).then(() => cache.resolveFunc(key)).then(val => expect(val).to.be(0));
});

it('should work when failing sync', () => {
  const errorMsg = 'A nasty error...';
  const key = cache.registerFunc(() => {
    throw new Error(errorMsg);
  });
  return cache.resolveFunc(key).then(() => {
    throw new Error('should have thrown exception!');
  }).catch(e => {
    if (e.message !== errorMsg) throw new Error('error message does not match!');
  });
});

it('should work when failing async', () => {
  const errorMsg = 'A nasty error...';
  const key = cache.registerFunc(() => Promise.reject(new Error(errorMsg)));
  return cache.resolveFunc(key).then(() => {
    throw new Error('should have thrown exception!');
  }).catch(e => {
    if (e.message !== errorMsg) throw new Error('error message does not match!');
  });
});

it('should be possible to invalidate cache', () => {
  let i = 0;
  const key = cache.registerFunc(() => i++);
  return cache.resolveFunc(key).then(val => {
    expect(val).to.be(0);
  }).then(() => {
    cache.invalidateFunc(key);
    return cache.resolveFunc(key);
  }).then(val => {
    expect(val).to.be(1);
  });
});

it('creating a same function with different keys', () => {
  let i = 0;
  const key1 = 'func1';
  const key2 = 'func2';

  const func = () => i++;

  cache.registerFunc(func, key1);
  cache.registerFunc(func, key2);
  return cache.resolveFunc(key1).then(val => {
    expect(val).to.be(0);
  }).then(() => cache.resolveFunc(key1)).then(val => {
    expect(val).to.be(0);
  }).then(() => cache.resolveFunc(key2)).then(val => {
    expect(val).to.be(1);
  }).then(() => {
    cache.invalidateFunc(key1);
    return cache.resolveFunc(key1);
  }).then(val => {
    expect(val).to.be(2);
  }).then(() => cache.resolveFunc(key2)).then(val => {
    expect(val).to.be(1);
  });
});

it('Testing ttl', function () {
  let i = 0;

  const func = () => i++;

  const key = cache.registerFunc(func, null, 1);
  this.timeout(3000);
  return cache.resolveFunc(key).then(val => {
    expect(val).to.be(0);
  }).then(() => new Promise(resolve => setTimeout(resolve, 500))).then(() => {
    return cache.resolveFunc(key);
  }).then(val => {
    expect(val).to.be(0);
  }).then(() => new Promise(resolve => setTimeout(resolve, 1000))).then(() => {
    return cache.resolveFunc(key);
  }).then(val => {
    expect(val).to.be(1);
  }).then(() => new Promise(resolve => setTimeout(resolve, 500))).then(() => cache.resolveFunc(key)).then(val => {
    expect(val).to.be(1);
  });
});

it('should be invokable', done => {
  const socketId = Math.floor(Math.random() * 1000);
  const remote = 'origin';
  const payload = {
    username: 'testuser',
    password: 'testpassword'
  };
  const server = http.createServer((req, res) => {
    try {
      const reqUrl = new URL(req.url, `http://${req.headers.host}`);
      expect(reqUrl.pathname).to.be('/api/credentials');
      expect(reqUrl.searchParams.get('remote')).to.be(`${remote}`);
      expect(reqUrl.searchParams.get('socketId')).to.be(`${socketId}`);
      res.writeHead(200, {
        'Content-Type': 'application/json'
      });
      res.end(JSON.stringify(payload));
    } finally {
      if (!res.writableFinished) {
        res.statusCode = 500;
        res.end();
      }
    }
  });
  server.listen({
    port: config.port
  }, () => {
    const command = `node bin/credentials-helper ${socketId} ${config.port} ${remote} get`;
    child_process.exec(command, (err, stdout, stderr) => {
      server.close();
      expect(err).to.not.be.ok();
      const ss = stdout.split('\n');
      expect(ss[0]).to.be(`username=${payload.username}`);
      expect(ss[1]).to.be(`password=${payload.password}`);
      done();
    });
  });
});

