it('diff on bare repository file should work', () => {
  // first add remote and push all commits
  return common.post(req, '/remotes/barerepository', {
    path: testDir,
    url: testBareDir
  }).then(() => common.post(req, '/push', {
    path: testDir,
    remote: 'barerepository'
  })).then(() => common.get(req, '/gitlog', {
    path: testDir
  })).then(res => {
    // find a commit which contains the testFile
    const commit = res.nodes.filter(commit => commit.fileLineDiffs.some(lineDiff => lineDiff.fileName == testFile))[0];
    return common.get(req, '/diff', {
      path: testDir,
      sha1: commit.sha1,
      file: testFile
    });
  });
});

