it('Add a file to .gitignore file through api call', () => {
  return common.createSmallRepo(req).then(dir => {
    const testFile = 'test.txt'; // Create .gitignore file prior to append

    return fs.writeFile(path.join(dir, '.gitignore'), 'test git ignore file...').then(() => common.post(req, '/testing/createfile', {
      file: path.join(dir, testFile)
    })).then(() => common.post(req, '/ignorefile', {
      path: dir,
      file: testFile
    })).then(() => {
      return common.get(req, '/status', {
        path: dir
      }).then(res => {
        expect(Object.keys(res.files).toString()).to.be('.gitignore');
      });
    }).then(() => {
      return fs.readFile(path.join(dir, '.gitignore')).then(data => {
        if (data.toString().indexOf(testFile) < 0) {
          throw new Error('Test file is not added to the .gitignore file.');
        }
      });
    });
  });
});

