it('JavaScript projects can be submitted and then viewed in /settings and on the certifications', {
  browser: 'electron'
}, () => {
  cy.fixture('../../config/curriculum.json').then(curriculum => {
    const targetBlock = 'javascript-algorithms-and-data-structures-projects';
    const javaScriptSuperBlock = Object.values(curriculum).filter(({
      blocks
    }) => blocks[targetBlock])[0];
    const {
      challenges,
      meta
    } = javaScriptSuperBlock.blocks[targetBlock];
    const projectTitles = meta.challengeOrder.map(([, title]) => title);
    const projectsInOrder = projectTitles.map(projectTitle => {
      return challenges.find(({
        title
      }) => title === projectTitle);
    }); // We need to wait for everything to finish loading and hydrating, so we
    // use this text as a proxy for that.

    const textInNextPage = projectTitles.slice(1); // The following text exists on the donation modal

    textInNextPage.push('Nicely done');
    projectsInOrder.forEach(({
      block,
      superBlock,
      dashedName,
      solutions
    }, i) => {
      const url = `/learn/${superBlock}/${block}/${dashedName}`;
      cy.visit(url);
      solutions.forEach(files => {
        files.forEach(({
          contents
        }) => {
          cy.get(selectors.editor).as('editor');
          cy.get('@editor').click().focused().type('{ctrl+a}{del}'); // NOTE: clipboard operations are flaky in watch mode, because
          // the document can lose focus

          cy.window().its('navigator.clipboard').invoke('writeText', contents);
          cy.document().invoke('execCommand', 'paste');
          cy.contains('Run the Tests').click();
          cy.contains('Submit and go to next challenge', {
            timeout: 8000
          }).click();
          cy.contains(textInNextPage[i]);
        });
      });
    });
    cy.visit('/settings');
    projectTitles.forEach(title => {
      cy.get(`[data-cy="${title}"]`).click(); // TODO: if we write a test to check that the solution is visible
      // before reloading, we should include that here.

      cy.contains('Solution for');
      cy.contains('Close').click();
    }); // Claim and view solutions on certification page

    cy.toggleAll();
    cy.visit('/settings');
    cy.get(`a[href="/certification/developmentuser/${projectsInOrder[0]?.superBlock}"]`).click();
    cy.contains('Show Certification').click();
    projectTitles.forEach(title => {
      cy.get(`[data-cy="${title} solution"]`).click(); // TODO: if we write a test to check that the solution is visible
      // before reloading, we should include that here.

      cy.contains('Solution for');
      cy.contains('Close').click();
    });
  });
});

