it('should resubmit failed updates, check they are stored, then flush', () => {
  store.set(failedUpdatesKey, failedUpdates);
  cy.request('http://localhost:3000/user/get-session-user').its('body.user.developmentuser.completedChallenges').then(completedChallenges => {
    const completedIds = getCompletedIds(completedChallenges);
    failedUpdates.forEach(failedUpdate => {
      expect(completedIds).not.to.include(failedUpdate.payload.id);
    });
  });
  cy.intercept('http://localhost:3000/modern-challenge-completed').as('completed');
  cy.wrap(store.get(failedUpdatesKey)).should('deep.equal', failedUpdates);
  cy.reload();
  cy.wait('@completed');
  // if we don't wait for both requests to complete, we have a race condition
  cy.wait('@completed');
  cy.request('http://localhost:3000/user/get-session-user').its('body.user.developmentuser.completedChallenges').then(completedChallenges => {
    const completedIds = getCompletedIds(completedChallenges);
    failedUpdates.forEach(failedUpdate => {
      expect(completedIds).to.include(failedUpdate.payload.id);
    });
    expect(store.get(failedUpdatesKey)).to.be.empty;
  });
});

it('locks and unlocks code checks when a user makes and fixes an error', () => {
  const workingOutput = 'Your test output will go here';
  // We're using this as a proxy for the lock, since we don't currently
  // disable the button when there's a syntax error (the checks will just not
  // be triggered)
  const brokenOutput = 'SyntaxError: unknown: Unexpected token';
  // Unlocked
  cy.get(selectors.dataCy.outputText).as('output').contains(workingOutput);
  // Lock with a syntax error
  cy.get(selectors.class.reactMonacoEditor).as('editor').click().focused().type(`{movetoend}var`);
  cy.get('@output').contains(brokenOutput);

  // Unlock by fixing the error.
  cy.get('@editor').click().focused().type(`{movetoend} x = 1;`);
  cy.get('@output').contains(workingOutput);
});

