test(`dev & build outputs have matching head elements from Head function export`, async () => {
  const devSsrHtml = await fetch(`http://localhost:8000/head-function-export`, {
    headers: {
      "x-gatsby-wait-for-dev-ssr": `1`
    }
  }).then(res => res.text());
  const devSsrDom = parse(devSsrHtml);
  const devSsrHead = devSsrDom.querySelector(`[data-testid=title]`);
  const ssrHtml = await fs.readFile(`${__dirname}/../public/head-function-export/index.html`, `utf8`);
  const ssrDom = parse(ssrHtml);
  const ssrHead = ssrDom.querySelector(`[data-testid=title]`);
  expect(devSsrHead.textContent).toEqual(ssrHead.textContent);
  expect(devSsrDom.querySelector(`html`).attributes).toEqual(ssrDom.querySelector(`html`).attributes);
  expect(devSsrDom.querySelector(`html`).attributes).toMatchInlineSnapshot(`
    Object {
      "data-foo": "bar",
      "lang": "fr",
    }
  `);
  expect(devSsrDom.querySelector(`body`).attributes).toEqual(ssrDom.querySelector(`body`).attributes);
  expect(devSsrDom.querySelector(`body`).attributes).toMatchInlineSnapshot(`
    Object {
      "data-foo": "baz",
    }
  `);
});

it(`should kill the develop process when exit command is given`, done => {
  expect.assertions(1);
  gatsbyProcess.on("exit", () => {
    expect(true).toBe(true);
    done();
  });
  let hasSentExitCode;
  gatsbyProcess.on("message", msg => {
    if (hasSentExitCode) {
      return;
    }
    hasSentExitCode = true;
    gatsbyProcess.send({
      type: "COMMAND",
      action: {
        type: "EXIT",
        payload: "SIGINT"
      }
    });
  });
});

it(`emit start, update and end events for a successful activity`, async () => {
  const activityEvents = events.filter(event => event.type === `LOG_ACTION` && event.action.payload.id === `Successful activity`);

  // console.log(util.inspect(activityEvents, { depth: null }))

  const activityUuid = activityEvents[0].action.payload.uuid;
  expect(activityEvents).toEqual([expect.objectContaining({
    type: `LOG_ACTION`,
    action: expect.objectContaining({
      type: `ACTIVITY_START`,
      payload: expect.objectContaining({
        id: `Successful activity`,
        uuid: activityUuid,
        total: 100
      })
    })
  }), expect.objectContaining({
    type: `LOG_ACTION`,
    action: expect.objectContaining({
      type: `ACTIVITY_UPDATE`,
      payload: expect.objectContaining({
        id: `Successful activity`,
        uuid: activityUuid,
        current: 50
      })
    })
  }), expect.objectContaining({
    type: `LOG_ACTION`,
    action: expect.objectContaining({
      type: `ACTIVITY_END`,
      payload: expect.objectContaining({
        id: `Successful activity`,
        uuid: activityUuid,
        status: `SUCCESS`
      })
    })
  })]);
});

