  })]));
  expect(events).toEqual(expect.arrayContaining([
  // Local plugin with require.resolve
  expect.objectContaining({
    type: `LOG_ACTION`,
    action: expect.objectContaining({
      type: `LOG`,
      payload: expect.objectContaining({
        level: `ERROR`,
        category: `USER`,
        context: expect.objectContaining({
          pluginName: expect.stringContaining("integration-tests/structured-logging/local-plugin-with-path/index.js"),
          validationErrors: expect.arrayContaining([{
            context: {
              key: "required",
              label: "required"
            },
            message: '"required" is required',
            path: ["required"],
            type: "any.required"
          }, {
            context: {
              key: "optionalString",
              label: "optionalString",
              value: 1234
            },
            message: '"optionalString" must be a string',
            path: ["optionalString"],
            type: "string.base"
          }])
        }),
        code: `11331`,
        type: `API.NODE.VALIDATION`
      })
    })
  }),
  // Local plugin with name in gatsby-config
  expect.objectContaining({
    type: `LOG_ACTION`,
    action: expect.objectContaining({
      type: `LOG`,
      payload: expect.objectContaining({
        level: `ERROR`,
        category: `USER`,
        context: expect.objectContaining({
          pluginName: "local-plugin",
          validationErrors: expect.arrayContaining([{
            context: {
              key: "required",
              label: "required"
            },
            message: '"required" is required',
            path: ["required"],
            type: "any.required"
          }, {
            context: {
              key: "optionalString",
