it(`Head function export receive correct props`, () => {
  cy.visit(headFunctionExportSharedData.page.correctProps).waitForRouteChange();
  const data = {
    site: {
      siteMetadata: {
        headFunctionExport: {
          ...headFunctionExportSharedData.data.queried
        }
      }
    }
  };
  const location = {
    pathname: headFunctionExportSharedData.page.correctProps
  };
  const pageContext = headFunctionExportSharedData.data.pageContext;
  cy.getTestElement(`pageContext`).invoke(`attr`, `content`).should(`equal`, JSON.stringify(pageContext, null, 2));
  cy.getTestElement(`location`).invoke(`attr`, `content`).should(`equal`, JSON.stringify(location, null, 2));
  cy.getTestElement(`data`).invoke(`attr`, `content`).should(`equal`, JSON.stringify(data, null, 2));
});

it(`Deduplicates multiple tags with same id`, () => {
  cy.visit(headFunctionExportSharedData.page.deduplication).waitForRouteChange();

  // deduplication link has id and should be deduplicated
  cy.get(`link[rel=deduplication]`).should("have.length", 1);
  // last deduplication link should win
  cy.get(`link[rel=deduplication]`).should("have.attr", "href", "/bar");
  // we should preserve id
  cy.get(`link[rel=deduplication]`).should("have.attr", "id", "deduplication-test");

  // alternate links are not using id, so should have multiple instances
  cy.get(`link[rel=alternate]`).should("have.length", 2);
});

