import { graphql } from "gatsby"

const Test = () => (
  <div></div>
)

export default Test

export function config() {
  const data = graphql\`{ __typename }\`

  return () => {
    return {
      defer: true
    }
  }
}
`);
  expect(run).toThrowErrorMatchingInlineSnapshot(`"unknown file: BabelPluginRemoveGraphQLQueries: the \\"config\\" export must be async when using it with graphql"`);
});

