it(`should transform Array-destructured hook return values use object destructuring`, () => {
  const output = babel(
    trim`
    import { useState } from 'react';
    const [count, setCount] = useState(0);
  `
  )

  expect(output).toMatch(trim`
  \"use strict\";var _react=require(\"react\");const{0:count,1:setCount}=(0,_react.useState)(0);
  `)

  expect(output).toMatchInlineSnapshot(
    `"\\"use strict\\";var _react=require(\\"react\\");const{0:count,1:setCount}=(0,_react.useState)(0);"`
  )
})

it(`should handle skipped items`, () => {
  const input = trim`
    import { useState } from 'react';
    const [, setCount] = useState(0);
  `

  expect(() => babel(input)).not.toThrow()
  expect(babel(input)).toMatchInlineSnapshot(
    `"\\"use strict\\";var _react=require(\\"react\\");const{1:setCount}=(0,_react.useState)(0);"`
  )
})

it(`reports an error when it s not possible to clone the repo`, async () => {
  ;(path as any).join.mockImplementation(() => `/somewhere-here`)
  ;(execa as any).mockImplementation(() => {
    throw new Error(`Not possible to clone the repo`)
  })

  try {
    await initStarter(
      `gatsby-starter-hello-world`,
      `./somewhere`,
      [],
      `A site`
    )
  } catch (e) {
    expect(execa).toBeCalledWith(`git`, [
      `clone`,
      `gatsby-starter-hello-world`,
      `--recursive`,
      `--depth=1`,
      `--quiet`,
    ])
    expect(reporter.panic).toBeCalledWith(`Not possible to clone the repo`)
    expect(reporter.success).not.toBeCalledWith(
      `Created site from template`
    )
    expect(fs.remove).toBeCalledWith(`/somewhere-here`)
  }
})

