  expect(getRewriteLinkPath(`always`)(`#hash`, `/`)).toBe(`/#hash`);
  expect(getRewriteLinkPath(`always`)(`?query_param=hello`, `/`)).toBe(`/?query_param=hello`);
  expect(getRewriteLinkPath(`always`)(`?query_param=hello#anchor`, `/`)).toBe(`/?query_param=hello#anchor`);
  expect(getRewriteLinkPath(`always`)(`/path#hash`, `/`)).toBe(`/path/#hash`);
  expect(getRewriteLinkPath(`always`)(`/path?query_param=hello`, `/`)).toBe(`/path/?query_param=hello`);
  expect(getRewriteLinkPath(`always`)(`/path?query_param=hello#anchor`, `/`)).toBe(`/path/?query_param=hello#anchor`);
  expect(getRewriteLinkPath(`always`, `/prefix`)(`/`, `/`)).toBe(`/prefix/`);
});

it(`handles never option`, () => {
  expect(getRewriteLinkPath(`never`)(`/path`, `/`)).toBe(`/path`);
  expect(getRewriteLinkPath(`never`)(`/path/`, `/`)).toBe(`/path`);
  expect(getRewriteLinkPath(`always`)(`#hash`, `/`)).toBe(`/#hash`);
  expect(getRewriteLinkPath(`always`)(`?query_param=hello`, `/`)).toBe(`/?query_param=hello`);
  expect(getRewriteLinkPath(`always`)(`?query_param=hello#anchor`, `/`)).toBe(`/?query_param=hello#anchor`);
