  expect(getRewriteLinkPath(`never`)(`/path/?query_param=hello#anchor`, `/`)).toBe(`/path?query_param=hello#anchor`);
  expect(getRewriteLinkPath(`never`, `/prefix`)(`/`, `/`)).toBe(`/prefix`);
});

it(`handles ignore option`, () => {
