  )
  expect(seven).toEqual(`foo/bar`)
  const { derivedPath: eight } = await derivePath(
    `foo/{Page.pathOne}/{Page.pathTwo}`,
    {
      pathOne: `/`,
      pathTwo: `/bar/`,
    },
    reporter
  )
  expect(eight).toEqual(`foo/bar/`)
  const { derivedPath: nine } = await derivePath(
    `foo/{Page.path}/[...name]`,
    {
      path: `/`,
    },
    reporter
  )
  expect(nine).toEqual(`foo/[...name]`)
})

