    msg.toEqual(expect.stringContaining(`Request ID: 123abc`));
    msg.toEqual(expect.stringContaining(`Attempts: 3`));
  }
  expect(reporter.panic).toBeCalled();
  expect(scope.isDone()).toBeTruthy();
});

